import React from "react";
import {
  Card,
  CardContent,
  Typography,
  Button,
  Stack,
  Avatar,
  Chip,
  Box,
} from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";

const UserCard = ({
  user,
  onAction,
  actionText,
  actionColor = "primary",
  mutualFriends,
  commonInterests = [],
}) => {
  return (
    <Card variant="outlined" sx={{ mb: 1 }}>
      <CardContent>
        <Stack direction="row" alignItems="flex-start" spacing={2}>
          <Avatar>
            <PersonIcon />
          </Avatar>
          <Stack flex={1}>
            <Typography variant="subtitle1">{user.username}</Typography>
            {mutualFriends > 0 && (
              <Chip
                label={`${mutualFriends} mutual friend${
                  mutualFriends > 1 ? "s" : ""
                }`}
                size="small"
                variant="outlined"
                sx={{ mt: 0.5, width: "fit-content" }}
              />
            )}
            {commonInterests.length > 0 && (
              <Box sx={{ mt: 1 }}>
                <Typography variant="caption" color="text.secondary">
                  Common Interests:
                </Typography>
                <Stack
                  direction="row"
                  spacing={1}
                  sx={{ mt: 0.5 }}
                  flexWrap="wrap"
                >
                  {commonInterests.map((interest) => (
                    <Chip
                      key={interest}
                      label={interest}
                      size="small"
                      variant="outlined"
                      color="secondary"
                    />
                  ))}
                </Stack>
              </Box>
            )}
            {user.interests?.length > 0 && (
              <Box sx={{ mt: 1 }}>
                <Typography variant="caption" color="text.secondary">
                  Interests:
                </Typography>
                <Stack
                  direction="row"
                  spacing={1}
                  sx={{ mt: 0.5 }}
                  flexWrap="wrap"
                >
                  {user.interests.map((interest) => (
                    <Chip
                      key={interest}
                      label={interest}
                      size="small"
                      variant="outlined"
                    />
                  ))}
                </Stack>
              </Box>
            )}
          </Stack>
          <Button
            variant="contained"
            color={actionColor}
            size="small"
            onClick={() => onAction(user._id)}
          >
            {actionText}
          </Button>
        </Stack>
      </CardContent>
    </Card>
  );
};

export default UserCard;
