import React, { useState, useEffect } from "react";
import {
  Container,
  Grid,
  Paper,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  TextField,
  Box,
} from "@mui/material";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const Home = () => {
  const [users, setUsers] = useState([]);
  const [friends, setFriends] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const { user } = useAuth();

  // Fetch users and friends
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [usersRes, friendsRes, recommendationsRes] = await Promise.all([
          axios.get("http://localhost:7777/api/users", {
            headers: { "x-auth-token": user },
          }),
          axios.get("http://localhost:7777/api/friends", {
            headers: { "x-auth-token": user },
          }),
          axios.get("http://localhost:7777/api/friends/recommendations", {
            headers: { "x-auth-token": user },
          }),
        ]);

        setUsers(usersRes.data);
        setFriends(friendsRes.data);
        setRecommendations(recommendationsRes.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    if (user) {
      fetchData();
    }
  }, [user]);

  // Handle friend request
  const handleFriendRequest = async (userId) => {
    try {
      await axios.post(
        `http://localhost:7777/api/friends/request/${userId}`,
        {},
        {
          headers: { "x-auth-token": user },
        }
      );
      // Refresh users list
      const usersRes = await axios.get("http://localhost:7777/api/users", {
        headers: { "x-auth-token": user },
      });
      setUsers(usersRes.data);
    } catch (error) {
      console.error("Error sending friend request:", error);
    }
  };

  // Handle unfriend
  const handleUnfriend = async (userId) => {
    try {
      await axios.delete(`http://localhost:7777/api/friends/${userId}`, {
        headers: { "x-auth-token": user },
      });
      // Refresh friends list
      const friendsRes = await axios.get("http://localhost:7777/api/friends", {
        headers: { "x-auth-token": user },
      });
      setFriends(friendsRes.data);
    } catch (error) {
      console.error("Error unfriending:", error);
    }
  };

  // Handle search
  const handleSearch = async () => {
    try {
      const res = await axios.get(
        `http://localhost:7777/api/users/search?q=${searchTerm}`,
        {
          headers: { "x-auth-token": user },
        }
      );
      setUsers(res.data);
    } catch (error) {
      console.error("Error searching users:", error);
    }
  };

  return (
    <Container>
      <Grid container spacing={3} sx={{ mt: 3 }}>
        {/* Search Section */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Box display="flex" gap={2}>
              <TextField
                fullWidth
                label="Search Users"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <Button variant="contained" onClick={handleSearch}>
                Search
              </Button>
            </Box>
          </Paper>
        </Grid>

        {/* Users List */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Users
            </Typography>
            <List>
              {users.map((user) => (
                <ListItem
                  key={user._id}
                  secondaryAction={
                    <Button
                      variant="contained"
                      size="small"
                      onClick={() => handleFriendRequest(user._id)}
                    >
                      Add Friend
                    </Button>
                  }
                >
                  <ListItemText primary={user.username} />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>

        {/* Friends List */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Friends
            </Typography>
            <List>
              {friends.map((friend) => (
                <ListItem
                  key={friend._id}
                  secondaryAction={
                    <Button
                      variant="outlined"
                      color="error"
                      size="small"
                      onClick={() => handleUnfriend(friend._id)}
                    >
                      Unfriend
                    </Button>
                  }
                >
                  <ListItemText primary={friend.username} />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>

        {/* Recommendations */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Recommended Friends
            </Typography>
            <List>
              {recommendations.map((user) => (
                <ListItem
                  key={user._id}
                  secondaryAction={
                    <Button
                      variant="contained"
                      size="small"
                      onClick={() => handleFriendRequest(user._id)}
                    >
                      Add Friend
                    </Button>
                  }
                >
                  <ListItemText
                    primary={user.username}
                    secondary={`${user.mutualFriends} mutual friends`}
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Home;
